def shellSort(arr):
    gap = len(arr)//2
    while gap>0:
        for i in range(gap, len(arr)):
            index = i
            value = arr[i]
            while index>=gap and arr[index-gap]>value:
                arr[index] = arr[index-gap]
                index = index-gap
            arr[index]=value
        gap = gap//2
    return arr
