def mergeSort(arr):
    if len(arr)<=1:
        return arr
    m = len(arr)//2
    left = mergeSort(arr[:m])
    right = mergeSort(arr[m:])
    return merge(left,right)
def merge(left,right):
    l,r = 0,0 # 
    result = []
    while l<len(left) and r<len(right):
        if left[l]<right[r]:
            result.append(left[l])
            l+=1
        else:
            result.append(right[r])
            r+=1
    result+=left[l:]
    result+=right[r:]
    return result
